import numpy as npimport pandas as pdimport seaborn as snsfrom matplotlib import rcParams# allow output to span multiple output lines in the consolepd.set_option('display.max_columns', 500)# switch to seaborn default stylistic parameters# see the useful https://seaborn.pydata.org/tutorial/aesthetics.htmlsns.set()sns.set_context('paper')   # 'talk' for slightly larger# change default plot sizercParams['figure.figsize'] = 9,7# 1 read datadf = pd.read_csv("https://archive.ics.uci.edu/ml/machine-learning-databases/wine-quality/winequality-red.csv", sep=";")# 2 make small version by selecting 20 random rowsdfs = df.sample(20)# 3 scale using z-scoredef z_score(x):    return (x-x.mean())/x.std()dfs = dfs.apply(z_score)# 4 find minimum values for each columndfs.min()# 5 find max for each columndfs.max()# 6 scale using unit interval normalizationdef unit(x):    return (x - x.min())/(x.max() - x.min())dfs = dfs.apply(unit)# 7 min & max for columnsdfs.min()dfs.max()# 8 read datadf = pd.read_csv("https://raw.githubusercontent.com/grbruns/cst383/master/cars-1978.csv", index_col='Car')